PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE `admins` (`id` UUIDV4 UNIQUE PRIMARY KEY, `username` VARCHAR(255) NOT NULL UNIQUE, `first_name` VARCHAR(255) NOT NULL, `last_name` VARCHAR(255) NOT NULL, `password` VARCHAR(255) NOT NULL, `company_name` VARCHAR(255) NOT NULL, `company_type` VARCHAR(255) NOT NULL, `admin_level` VARCHAR(255) NOT NULL, `reset_token` VARCHAR(255) DEFAULT '', `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `meals` (`id` UUIDV4 UNIQUE PRIMARY KEY, `created_by_id` UUIDV4 NOT NULL REFERENCES `admins` (`id`), `updated_by_id` UUIDV4 NOT NULL REFERENCES `admins` (`id`), `title` VARCHAR(255) NOT NULL, `image` UUIDV4[], `video` UUIDV4[], `max_eta` INTEGER, `is_available` TINYINT(1) NOT NULL DEFAULT 1, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `preferences` (`id` UUIDV4 UNIQUE PRIMARY KEY, `updated_by_id` UUIDV4 NOT NULL REFERENCES `admins` (`id`), `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `reviews` (`id` UUIDV4 UNIQUE PRIMARY KEY, `created_by_id` UUIDV4, `meal_id` UUIDV4 NOT NULL REFERENCES `meals` (`id`), `rating` INTEGER NOT NULL, `review` VARCHAR(255), `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `videos` (`id` UUIDV4 UNIQUE PRIMARY KEY, `path` VARCHAR(255) NOT NULL, `parent_name` VARCHAR(255) DEFAULT NULL, `parent_id` UUIDV4 DEFAULT NULL, `created_by_id` UUIDV4 NOT NULL REFERENCES `admins` (`id`), `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `images` (`id` UUIDV4 UNIQUE PRIMARY KEY, `path` VARCHAR(255) NOT NULL, `parent_name` VARCHAR(255) DEFAULT NULL, `parent_id` UUIDV4 DEFAULT NULL, `created_by_id` UUIDV4 NOT NULL REFERENCES `admins` (`id`), `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `temps` (`id` VARCHAR(255) NOT NULL UNIQUE PRIMARY KEY, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `admins_backup` (`id` UUIDV4 UNIQUE PRIMARY KEY, `username` VARCHAR(255) NOT NULL UNIQUE, `first_name` VARCHAR(255) NOT NULL, `last_name` VARCHAR(255) NOT NULL, `password` VARCHAR(255) NOT NULL, `company_name` VARCHAR(255) NOT NULL, `company_type` VARCHAR(255) NOT NULL, `admin_level` VARCHAR(255) NOT NULL, `reset_token` VARCHAR(255) DEFAULT '', `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
COMMIT;
